import React from 'react';
import ReactDOM from 'react-dom/client';
import { PieChart, Pie, Sector, Cell } from 'recharts';


class Router extends React.Component {




  render() {

const data = [
      { color: "Red", value: redCounter },
      { color: "Yellow", value: yellowCounter },
      { color: "Green", value: greenCounter }
    ];


const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

const RADIAN = Math.PI / 180;
const renderCustomizedLabel = ({ cx, cy, midAngle, innerRadius, outerRadius, percent, index }) => {
  const radius = innerRadius + (outerRadius - innerRadius) * 0.5;
  const x = cx + radius * Math.cos(-midAngle * RADIAN);
  const y = cy + radius * Math.sin(-midAngle * RADIAN);

  return (
    <text x={x} y={y} fill="white" textAnchor={x > cx ? 'start' : 'end'} dominantBaseline="central">
      {`${(percent * 100).toFixed(0)}%`}
    </text>
  );
};

    const routerInfoList = [
      { id: 0, type: "NPU-1", usage: 94, color: '' },
      { id: 1, type: "NPU-0", usage: 70, color: '' },
      { id: 2, type: "NPU-0", usage: 2, color: '' },
      { id: 3, type: "NPU-1", usage: 35, color: '' },
      { id: 4, type: "NPU-0", usage: 99, color: '' }
    ];

    let redCounter = 0;
    let yellowCounter = 0;
    let greenCounter = 0;

    for (let i = 0; i < routerInfoList.length; i++) {
      if (routerInfoList[i].usage >= 95) {
        routerInfoList[i].color = "red";
        redCounter++;
      }
      if (routerInfoList[i].usage >= 80 && routerInfoList[i].usage < 95) {
	routerInfoList[i].color = "yellow";
        yellowCounter++;
      }
      if (routerInfoList[i].usage >= 0 && routerInfoList[i].usage < 80) {
        routerInfoList[i].color = "green";
	greenCounter++;
      }
    }

    let list = routerInfoList.map (
      (el) => {
        return el.color
      }
    );










    return (
      <div>
        <p>{list}</p>
	<h1>Usage Color</h1>
        <PieChart width={400} height={400}>
          <Pie
            data={data}
            cx="50%"
            cy="50%"
            labelLine={false}
            label={renderCustomizedLabel}
            outerRadius={80}
            fill="#8884d8"
            dataKey="value"
          >
            {data.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
            ))}
          </Pie>
        </PieChart>
      </div>
    )
  }
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Router />);

export default Router;
