import React from 'react';
import ReactDOM from 'react-dom/client';


class Hobbies extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      hobbies: ['soccer', 'basketball', 'baseball', 'football'],
      userInput: ''
    };
  }

  addHobby() {
    const oldHobbies = this.state.hobbies;
    this.setState({
      hobbies: oldHobbies.concat(this.state.userInput),
      userInput: ''
    });
  }

  changeUserInput(event) {
    this.setState({
      userInput: event.target.value
    });
  }

  removeHobby(hobby) {
    const oldHobbies = this.state.hobbies;
    const position = oldHobbies.indexOf(hobby);
    this.setState({
      hobbies: oldHobbies.filter(
        (el, index) => {
          return index != position
        }
      )
    });
  }




  render() {

    let list = this.state.hobbies.map(
      (hobby) => {
        return <li onClick={ () => { this.removeHobby(hobby) } }>{hobby}</li>
      }
    );


    return (
      <div>
        <p>My hobbies</p>
        <ul>
          {list}
        </ul>
        <input type="text" value={this.state.userInput} onChange={this.changeUserInput.bind(this)}></input>
        <button onClick={this.addHobby.bind(this)}>New Hobby</button>
      </div>
    )
  }
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Hobbies />);
